INSERT INTO `faqs` (`id`, `description`, `description2`, `datas_id`, `categories_id`, `created_at`, `updated_at`, `source`, `source_link`) VALUES
(1, '<p>Waiver request submitted to CMS on 6/29/2018; revised waiver requests submitted to CMS on 7/31/2018 and 9/10/2018</p>', NULL, 1, 1, '2018-11-12 08:05:04', '2019-03-26 05:14:22', NULL, NULL),
(2, '<p><strong>FFS </strong></p>\r\n\r\n<ul>\r\n	<li>Parents and caretakers with incomes from 0-18% FPL</li>\r\n</ul>', NULL, 1, 2, '2018-11-12 08:06:08', '2018-11-12 08:06:08', NULL, NULL),
(3, '<p>No</p>', NULL, 1, 3, '2018-11-12 08:08:20', '2018-11-14 07:53:52', NULL, NULL),
(4, '<ul>	\r\n	<li>State plan cost-sharing</li>\r\n</ul>', NULL, 1, 4, '2018-11-12 08:09:39', '2019-03-26 05:33:15', NULL, NULL),
(5, '<p>No</p>', NULL, 1, 5, '2018-11-12 08:10:20', '2018-11-12 08:10:20', NULL, NULL),
(6, '<p>No</p>', NULL, 1, 13, '2018-11-12 08:10:47', '2019-03-26 05:52:38', NULL, NULL),
(7, '<p>No</p>', NULL, 1, 6, '2018-11-12 08:11:36', '2018-11-12 08:11:36', NULL, NULL),
(8, '<ul>\r\n	<li>Waiver extension approved on 9/30/2016; effective 10/1/2016 to 9/30/2021</li>\r\n	<li>Waiver amendment approved on 1/18/2019</li>\r\n</ul>', '<p>Waiver amendment request submitted to CMS on 12/19/2017; second amendment request for waiver of retroactive coverage submitted to CMS on 4/6/2018</p>', 2, 1, '2018-11-12 08:13:22', '2019-03-26 06:09:16', NULL, NULL),
(9, '<ul>\r\n	<li>Requests ability to condition Medicaid eligibility for non-disabled adults ages 19-59 on work/CE requirements</li>\r\n	<li>Individuals subject to work/CE requirements must participate in community engagement activities (e.g., employment, on-the-job training, job search and job-readiness activities, education, volunteering, or technical training); number of hours required varies based on the age of their dependent children: 20 hours/week for individuals with dependent children under age six and 35 hours/week for all other individuals subject to the requirement</li>\r\n	<li>Proposed exempt populations include: individuals with a disability (includes individuals receiving SSI or SSDI), medically frail individuals, individuals with a medical condition that prevents them from complying with work/CE requirements, pregnant women, women who recently gave birth, individuals required to care for a disabled child or adult, individuals participating in active SUD treatment, individuals enrolled in and compliant or exempt from TANF work requirements (includes individuals who meet a good cause exemption under TANF), parents or caretakers of a child age 12 months or younger, parents and caretakers of a child under age six for whom childcare is not available</li>\r\n	<li>Individuals who do not comply with work/CE requirements or demonstrate proof of an exemption within 90 days of becoming subject to the work requirement are dis-enrolled</li>\r\n	<li>After initial 90-day period, individuals must comply with the work requirement each month; individuals who fail to comply with work/CE requirements in a given month are dis-enrolled after a 30-day grace period</li>\r\n	<li>Individuals whose incomes increase above Medicaid limits are eligible for 18 months of TMA</li>\r\n</ul>', NULL, 1, 7, '2018-11-12 08:18:13', '2019-03-26 05:37:10', NULL, NULL),
(10, '<p>No</p>', NULL, 1, 8, '2018-11-12 08:18:47', '2018-11-12 08:18:47', NULL, NULL),
(11, '<p>No</p>', NULL, 1, 9, '2018-11-12 08:19:56', '2018-11-12 08:19:56', NULL, NULL),
(13, '<p><strong>MMC</strong></p>\r\n\r\n<ul>\r\n	<li>Nearly all Medicaid enrollees with limited exceptions</li>\r\n	<li>AHCCCS CARE Accounts (similar to an HSA) funded by enrollee and/or third-party contributions</li>\r\n</ul>', '<p><strong>MMC</strong></p>\r\n\r\n<ul>\r\n	<li>New proposed waiver features apply to expansion adults with incomes from 0-138%</li>\r\n</ul>', 2, 2, '2018-11-12 08:20:38', '2019-03-26 06:11:00', NULL, NULL),
(14, '<p><a href="http://www.medicaid.alabama.gov/documents/2.0_Newsroom/2.7_Special_Initiatives/2.7.5_Work_Requirements/2.7.5_Alabama_Medicaid_Workforce_Initiative_1115_Application_6-29-18.pdf">Original Waiver Application</a>;<a href="https://www.medicaid.gov/Medicaid-CHIP-Program-Information/By-Topics/Waivers/1115/downloads/al/al-workforce-initiative-pa.pdf">Revised Waiver Application</a></p>', NULL, 1, 11, '2018-11-12 08:21:48', '2019-03-26 05:47:23', NULL, NULL),
(15, '<p>N/A</p>', NULL, 1, 19, '2018-11-12 08:22:28', '2019-03-26 05:50:58', NULL, NULL),
(16, '<ul>\r\n	<li>For expansion adults with incomes &gt;100% FPL (medically frail individuals, individuals with a SMI, and American Indians/Alaska Natives are exempt)</li>\r\n	<li>Lesser of 2% of income or $25</li>\r\n	<li>In the form of a contribution to AHCCCS Care Account</li>\r\n	<li>Individuals who fail to pay premiums within a two-month grace period dis-enrolled; may reenroll at any time and do not have to pay back premiums</li>\r\n	<li>Individuals may request a hardship exemption if a household member dies or household has qualifying expenses &gt;10% of household income during the previous month</li>\r\n	<li>Only individuals making timely premium payments may access AHCCCS CARE Account funds</li>\r\n</ul>', '<p>No proposed changes</p>', 2, 3, '2018-11-12 08:28:02', '2019-03-26 06:12:57', NULL, NULL),
(17, '<ul>\r\n	<li>For expansion adults with incomes &gt; 100% FPL (medically frail individuals, individuals with a SMI, and American Indians/Alaska Natives are exempt)</li>\r\n	<li>Co-payments up to 3% of income</li>\r\n	<li>Co-payments limited to non-emergency use of the ER, opioids, specialist services without PCP referral, and brand name drugs when a generic is available</li>\r\n	<li>Co-payments charged on a retrospective basis</li>\r\n	<li>Only individuals making timely co-payments may access AHCCCS CARE Account funds</li>\r\n</ul>', '<p>No proposed changes</p>', 2, 4, '2018-11-12 08:29:26', '2019-03-26 06:14:08', NULL, NULL),
(18, '<ul>\r\n	<li>May eliminate premiums and cost-sharing obligations for six months</li>\r\n	<li>May enable access to AHCCCS CARE Account funds</li>\r\n	<li>May enable individuals to rollover AHCCCS CARE Account funds</li>\r\n</ul>', '<p>No proposed changes</p>', 2, 5, '2018-11-12 09:18:53', '2019-03-26 06:22:10', NULL, NULL),
(19, '<ul>\r\n	<li>Requests waiver of the IMD exclusion for individuals receiving mental health and SUD services regardless of length of stay (as part of separate waiver amendment application)</li>\r\n</ul>', '<ul>\r\n	<li>Requests waiver of the IMD exclusion for individuals receiving mental health and SUD services regardless of length of stay (as part of separate waiver amendment application)</li>\r\n</ul>', 2, 13, '2018-11-12 09:20:51', '2019-03-26 06:22:53', NULL, NULL),
(20, '<ul>\r\n	<li>Retroactive coverage waived (pregnant women, women &le;60 days postpartum, and children &lt; age 19 exempt)</li>\r\n</ul>', '<ul>\r\n	<li>Requests waiver of retroactive coverage</li>\r\n	<li>Requests five-year lifetime Medicaid enrollment limit for individuals subject to work/CE requirements; months that an individual complies with work requirement or 5 is exempt do not count towards limit</li>\r\n	<li>Proposes twice annual redeterminations for individuals subject to work/CE requirements</li>\r\n</ul>', 2, 6, '2018-11-12 09:22:01', '2019-03-26 06:23:28', NULL, NULL),
(21, '<ul>\r\n	<li>Waiver initially approved on 9/27/2013; effective 1/1/2014 to 12/31/2016</li>\r\n	<li>Waiver extension and amendment request approved on 12/8/2016; effective 1/1/2017 to 12/31/2021</li>\r\n	<li>Waiver amendment request approved on 3/5/2018</li>\r\n</ul>', NULL, 3, 1, '2018-11-12 09:23:40', '2018-11-14 08:13:36', NULL, NULL),
(22, '<p><strong>QHP Premium Assistance</strong></p>\r\n\r\n<ul>\r\n	<li>Expansion adults with incomes from 0-138% FPL</li>\r\n	<li>Medically frail individuals are excluded</li>\r\n</ul>', NULL, 3, 2, '2018-11-12 09:24:28', '2018-11-14 08:13:59', NULL, NULL),
(23, '<ul>\r\n	<li>For individuals with incomes &gt;100% FPL</li>\r\n	<li>Up to 2% of income</li>\r\n	<li>Payment is not a condition of eligibility</li>\r\n	<li>Nonpayment results in debt to the state</li>\r\n</ul>', NULL, 3, 3, '2018-11-12 09:25:13', '2018-11-14 08:14:37', NULL, NULL),
(24, '<ul>\r\n	<li>For individuals with incomes &gt;100% FPL</li>\r\n	<li>Cost-sharing subject to maximum permitted Medicaid cost-sharing levels</li>\r\n</ul>', NULL, 3, 4, '2018-11-12 09:26:27', '2019-03-26 06:54:40', NULL, NULL),
(25, '<ul>\r\n	<li>Individuals may receive incentive benefit for engaging with a PCP if authorized via a subsequent waiver amendment</li>\r\n	<li>Individuals with incomes &gt;100% FPL must also make timely premium payments to be eligible for incentive benefit</li>\r\n</ul>', NULL, 3, 5, '2018-11-12 09:27:04', '2018-11-14 08:18:32', NULL, NULL),
(26, '<ul>\r\n	<li>No coverage of non-emergency use of the ER for expansion adults</li>\r\n</ul>', NULL, 3, 13, '2018-11-12 09:28:33', '2019-03-26 06:56:01', NULL, NULL),
(27, '<ul>\r\n	<li>Conditions Medicaid eligibility for adults ages 19-49 on work/CE requirements</li>\r\n	<li>Individuals subject to work/CE requirements must participate in &ge;80 hours/month of employment or education activities (e.g., employment, job search activities, education, life skills training, health education classes, community services, etc.); individuals transitioning from the justice system, living in areas with high-unemployment, or facing barriers to finding work may participate in community service to meet requirement</li>\r\n	<li>Exempt populations include: members of federal recognized tribes, pregnant women, women &le;60 days postpartum, former foster care children under age 26, individuals with SMI, individuals receiving temporary or permanent disability benefits, full-time students, victims of domestic violence, homeless individuals, parents or caregivers of a child under age 18 or who is 18 and a full-time student (limited to one exemption per household), caregivers of an individual with a disability, medically frail individuals, individuals in SUD treatment, individuals who are unable to work because of a physical or behavioral health condition, individuals with a disability, individuals receiving SNAP, TANF, or unemployment insurance, and individuals participating in other AHCCCS-approved work programs</li>\r\n	<li>Individuals have three-month grace period prior to being required to comply with work/CE requirements; after three-month grace period, individuals who fail to comply with requirements have their benefits suspended for two months; state will automatically reactivate coverage at end of two-month period</li>\r\n	<li>Individuals may re-activate suspended coverage early by meeting a &ldquo;good cause&rdquo; exemption (e.g., disability, illness, illness of household member, severe inclement weather, or family emergency), qualifying for an exemption, or becoming eligible for an eligibility group not subject to work/CE requirements</li>\r\n</ul>', '<ul>\r\n	<li>Requests ability to condition Medicaid eligibility for adults ages 19-54 on work/CE requirements</li>\r\n	<li>Individuals subject to work/CE requirements must participate in &ge;20 hours/week of employment or education activities (e.g.,employment, job-search activities, education, or participation in Employment Support and Development courses); individuals transitioning from the justice system, living in areas with high-unemployment, or facing barriers to finding work may participate in community service to meet requirement</li>\r\n	<li>Proposed exempt populations include: American Indians, women who recently gave birth, former foster care children under age 26, individuals with SMI, individuals receiving temporary or permanent disability benefits, full time students, victims of domestic violence, homeless individuals, individuals recently impacted by a catastrophic event, parents or caregivers of a child under age 13, caregivers of a family member enrolled in the Arizona Long-Term Care System, and medically frail individuals</li>\r\n	<li>Individuals who initially fail to comply with work/CE requirements dis-enrolled after a 6- month grace period; may re-enroll upon meeting work/CE requirements for 30 consecutive days</li>\r\n	<li>Individuals who report a change in circumstances that results in non-compliance with work/CE requirements (e.g., job loss) or are determined non-compliant at redetermination will have 3-month grace period to comply prior to disenrollment; State proposes conducting redetermination at the end of the 3-month grace period</li>\r\n	<li>Requests expenditure authority for federal match for costs associated with the design, development, installation, operation, and administration of systems (both Medicaid and non-Medicaid) necessary to implement work/CE requirements</li>\r\n	<li>Requests ability to condition Medicaid eligibility on completion of Medicaid application questions on compliance with workrequirements and work requirements exemption criteria</li>\r\n</ul>', 2, 7, '2018-11-12 09:30:14', '2019-03-26 06:27:41', NULL, NULL),
(28, '<ul>\r\n	<li>Limited waiver of retroactive coverage; state must provide retroactive coverage for 30 days instead of three months</li>\r\n</ul>', NULL, 3, 6, '2018-11-12 09:30:35', '2019-03-26 06:59:46', NULL, NULL),
(29, '<p>Yes (AHCCCS CARE Account)</p>\r\n\r\n<ul>\r\n	<li>Populations exempt from or not subject to premiums (e.g., expansion adults and parents/caretakers with incomes &le;100% FPL, pregnant women, individuals with an SMI, and American Indian/Alaska Natives) may opt-in to an AHCCCS CARE Account if it is funded by a third party or if they choose to pay premiums; if they opt in, they are not subject to penalties for failure to pay premiums or cost-sharing, and are not required to participate in healthy behaviors or continuously contribute to access AHCCCS Care Account funds</li>\r\n</ul>', '<p>No proposed changes</p>', 2, 8, '2018-11-12 09:31:01', '2019-03-26 06:29:04', NULL, NULL),
(30, '<p>No</p>', '<p>No proposed changes</p>', 2, 9, '2018-11-12 09:31:32', '2018-11-12 09:31:32', NULL, NULL),
(32, '<p>Waiver request submitted to CMS on 8/1/2017</p>', NULL, 4, 1, '2018-11-12 09:33:50', '2018-11-12 09:33:50', NULL, NULL),
(33, '<ul>\r\n	<li>Conditions Medicaid eligibility for adults ages 19-49 on work/CE requirements</li>\r\n	<li>Individuals subject to work/CE requirements must participate in &ge;80 hours/month of work/CE activities (e.g., employment, job search and training activities, education, vocational training, community service or volunteering, health care-related classes, activities available through the Arkansas Department of Workforce Services, or employment activities available under SNAP or TANF); individuals must report on compliance via electronic portal</li>\r\n	<li>Exempt populations include pregnant women, women who recently gave birth, medically frail individuals, full-time students, individuals exempt from work/CE requirements in SNAP or TANF, individuals who receive TANF benefits, individuals with a short-term incapacitation, individuals who are medically certified as physically or mentally unable to work, individuals with an acute medical condition that prevents them from complying with the work requirement, individuals caring for an incapacitated person, individuals living with a minor dependent child under age 17, individuals receiving unemployment benefits, and individuals in active SUD treatment</li>\r\n	<li>Individuals who fail to comply with work/CE requirements for three consecutive or nonconsecutive months within a plan year are dis-enrolled; individuals may not re-enroll until start of next plan year unless they turn age 50, qualify for another Medicaid eligibility category, or would have qualified for a good cause exemption but could not report it due to a catastrophic event/circumstances beyond their control</li>\r\n	<li>Months in which an individual meets a good cause exemption (e.g., disability, hospitalization, hospitalization of household member, birth or death of a household member, severe inclement weather, or family emergency) do not count as a month of non-compliance</li>\r\n	<li>State must submit eligibility and enrollment monitoring plan to CMS within 90 days of waiver&nbsp;approval to &ldquo;allow CMS to track Arkansas&#39; compliance with the assurances described in the STCs, including several related to eligibility and application processing systems;&rdquo; state may not dis-enroll any individuals for non-compliance with work requirement until CMS approves plan</li>\r\n</ul>', NULL, 3, 7, '2018-11-12 09:34:06', '2019-03-26 07:01:42', NULL, NULL),
(34, '<p><strong>FFS</strong></p>\r\n\r\n<ul>\r\n	<li>Parents and caretakers with incomes from 0-105% FPL</li>\r\n	<li>Individuals eligible for TMA with incomes from 0-185% FPL</li>\r\n	<li>Individuals eligible for family planning services with incomes from 0-209% FPL</li>\r\n	<li>Reasonable classifications of individuals under age 21 with incomes from 0-156% FPL</li>\r\n	<li>Medically needy individuals ages 18-20 with incomes from 0-156% FPL</li>\r\n	<li>Medically needy parents and caretakers with incomes from 0-100% FPL</li>\r\n	<li>Special benefits waiver (HIV) population with incomes from 0-250% FPL</li>\r\n	<li>Former foster care children up to age 26</li>\r\n</ul>', NULL, 4, 2, '2018-11-12 09:35:17', '2018-11-12 09:35:17', NULL, NULL),
(35, '<ul>\r\n	<li>For individuals with incomes from &ge;51% FPL</li>\r\n	<li>$10/month for individuals with incomes from 51-100% FPL; $20/month for individuals with incomes from 101-150% FPL; $30/month for individuals with incomes from 151-200% FPL; $40/month for individuals with incomes &ge;201% FPL (HIV waiver enrollees exempt; other exemptions same as work/CE requirements)</li>\r\n	<li>Individuals who fail to fail to pay premiums dis-enrolled with no grace period and are not permitted to re-enroll for 90-days unless they pay all past due premiums</li>\r\n</ul>', NULL, 4, 3, '2018-11-12 09:36:11', '2018-11-12 09:36:11', NULL, NULL),
(36, '<ul>\r\n	<li>State plan cost-sharing</li>\r\n	<li>$10 co-pay for non-emergency ER visits</li>\r\n</ul>', NULL, 4, 4, '2018-11-12 09:36:43', '2018-11-12 09:36:43', NULL, NULL),
(37, '<p>No</p>', NULL, 4, 5, '2018-11-12 09:37:04', '2018-11-12 09:37:04', NULL, NULL),
(38, '<p>No</p>', NULL, 4, 13, '2018-11-12 09:37:29', '2018-11-12 09:37:29', NULL, NULL),
