(134, '<ul>\r\n	<li>Waiver initially approved on 3/4/2015; effective 1/1/2016 to 12/31/2018</li>\r\n	<li>Waiver amendment approved on 5/7/2018</li>\r\n	<li>Waiver amendment and extension approved on 11/30/2018; effective 1/1/2019 to 12/31/2023</li>\r\n</ul>', '<p>Waiver extension and amendment request posted for public comment on 5/8/2018</p>', 12, 1, '2018-11-12 10:28:58', '2019-03-26 09:47:36', NULL, NULL),
(135, '<p><strong>MMC1</strong></p>\r\n\r\n<ul>\r\n	<li>Expansion adults with incomes from 0-138% FPL</li>\r\n	<li>Medically frail individuals are included</li>\r\n</ul>', '<p><strong>MMC</strong></p>\r\n\r\n<ul>\r\n	<li>Seeks to transition individuals enrolled in QHP premium assistance to MMC; enrollment will be mandatory</li>\r\n	<li>Medically frail individuals are included</li>\r\n</ul>\r\n\r\n<p><strong>ESI Premium Assistance</strong></p>\r\n\r\n<ul>\r\n	<li>Voluntary for expansion adults with incomes from 0-138% FPL</li>\r\n</ul>', 12, 2, '2018-11-12 10:31:39', '2019-03-26 09:48:06', NULL, NULL),
