(239, '<p><strong>Drug Use Screening Requirement</strong></p>\r\n\r\n<ul>\r\n	<li>Request to condition Medicaid eligibility for childless adults on completion of a drug use screening, and for individuals with a positive screening result, to condition Medicaid eligibility on a drug test or entering substance use disorder treatment</li>\r\n</ul>', '<p>N/A</p>', 19, 19, '2018-11-12 13:00:16', '2019-03-26 06:46:25', NULL, NULL),
(240, '<p>Yes</p>\r\n\r\n<ul>\r\n	<li>12-months guaranteed coverage</li>\r\n	<li>Provides subsidies of up to $150/month for adults and up to $140/month for CHIP-eligible children with access to ESI</li>\r\n</ul>', '<p><em><strong>August 2017 Waiver Amendment</strong></em></p>\r\n\r\n<ul>\r\n	<li>No proposed changes</li>\r\n</ul>\r\n\r\n<p><em><strong>June 22, 2018 Waiver Amendment</strong></em></p>\r\n\r\n<ul>\r\n	<li>For adults with incomes 0-100%, premium assistance would cover full ESI premium amount and would wrap cost sharing and benefits not provided by ESI</li>\r\n	<li>No proposed changes for individuals with incomes from 100-200% FPL</li>\r\n</ul>\r\n\r\n<p><em><strong>June 29, 2018 Waiver Amendment</strong></em></p>\r\n\r\n<ul>\r\n	<li>No proposed changes</li>\r\n</ul>', 18, 9, '2018-11-12 13:00:39', '2019-03-26 07:33:51', NULL, NULL),
